(31, 'habit-moment-survey', 'marketing', 'en_US', 'We\'re all ears! Share your experience so far with WooCommerce', 'We\'d love your input to shape the future of WooCommerce together. Feel free to share any feedback, ideas or suggestions that you have.', '{}', 'pending', 'woocommerce.com', '2021-11-22 16:36:39', NULL, 0, 'plain', '', 0, 'info'),
(32, 'wc-refund-returns-page', 'info', 'en_US', 'Setup a Refund and Returns Policy page to boost your store\'s credibility.', 'We have created a sample draft Refund and Returns Policy page for you. Please have a look and update it to fit your store.', '{}', 'unactioned', 'woocommerce-core', '2021-11-22 16:36:42', NULL, 0, 'plain', '', 0, 'info'),
